mapper.insert:

        INSERT INTO	lc_board (
			b_title,
			b_content,
			b_writer
      ) VALUES (
         #{bTitle},
         #{bContent},
         #{bWriter}
         )

mapper.update:
	 	UPDATE 			lc_board
 		SET
    				  b_title = #{bTitle},
    				  b_content = #{bContent}

 		WHERE			 b_id =  #{bId}
